mapper.insert:
insert into to_member(
	member_no, memeber_id, member_pass, member_email, member_name
) values(
	To_Member_SEQ.nextval, #{member_id}, #{member_pass}, #{member_email}, #{member_name};
);

insert into to_member(
	member_no, member_id, member_pass, member_email, member_name
)
values(
	member_no_seq.nextval, #{member_id}, #{member_pass}, #{member_email}, #{member_name}
)

mapper.update:
update to_member 
set member_email = #{member_email},
	member_pass = #{member_pass},
	member_name = #{member_name}
where member_id = #{member_id}
